function createBinaryObject(root, name, varname, unique_id, payload_on = "ON", payload_off = "OFF")
{
    let vjson = {
        "stat_t": "~/state",
        "dev": {
            "manufacturer": "PA1DVB"
        }
    };
    vjson["~"] = root;
    vjson["name"] = name;
    vjson["payload_on"] = payload_on;
    vjson["payload_off"] = payload_off;
    vjson["uniq_id"] = unique_id;
    vjson["val_tpl"] = "{{ value_json." + varname + " }}";
    return vjson;
}

