// check the SOC near 10% to prevent dropping below
let data = context.flow.data || {};
let soc = data["batt_soc"];
let ess_setpoint = data["ess_setpoint"];
let batt_mode = data["batt_mode"];
let prev_soc = context.flow.prev_soc || 12;
let batt_low_limit = context.flow.batt_low_limit

if (soc <= batt_low_limit && batt_mode == "Idle") {
    if (soc < prev_soc) {
        // soc is lower than last time so increase power to compensate
        msg.payload=ess_setpoint + 20;
        context.flow.prev_soc = soc;
        return msg;
    }
    else {
        if (soc > prev_soc) {
            // soc is increasing again so lower slightly to converge to soc == prev_soc
            msg.payload=ess_setpoint - 10;
            context.flow.prev_soc = soc;
            return msg;
        }
    }
}

